import {
    FETCH_QUOTATIONS_START,
    FETCH_QUOTATIONS_SUCCESS,
    FETCH_QUOTATIONS_FAILURE
} from 'actionTypes';
import { fetchQuotations as fetchQuotationsApi } from 'api';

export const fetchQuotations = () => async dispatch => {
    dispatch({type: FETCH_QUOTATIONS_START})

    try {
        const quotations = await fetchQuotationsApi()
        dispatch({
            type: FETCH_QUOTATIONS_SUCCESS,
            payload: quotations
        })
    } catch (err) {
        dispatch({
            type: FETCH_QUOTATIONS_FAILURE,
            payload: err,
            error: true
        })
    }

}