import R from 'ramda';

import {
    FETCH_QUOTATIONS_SUCCESS
} from 'actionTypes';

const initialState = {
    pair: []
}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_QUOTATIONS_SUCCESS: 
            return R.merge(state, {
                pair: R.keys(payload)
            });
        default:
            return state;
    }
}